package com.example.eshop.Activity;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;

import android.os.Bundle;

import com.example.eshop.adapters.CategotryAdapter;
import com.example.eshop.adapters.ProductAdapter;
import com.example.eshop.databinding.ActivityMainBinding;
import com.example.eshop.models.Category;
import com.example.eshop.models.Product;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {
     ActivityMainBinding binding;



     // categories
     CategotryAdapter categoryAdapter;
     ArrayList<Category> categories;

     // products
     ProductAdapter productAdapter; // from adapter package
     ArrayList<Product> products; // from model package

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        binding = ActivityMainBinding.inflate(getLayoutInflater());
        setContentView(binding.getRoot());




        initCategory();
        initProduct();




    }


    void initCategory(){
        String icon="data:image/png;base64,";
        categories=new ArrayList<>();
        categories.add(new Category("sports",icon,"some descriptions","#18ab4e",1));
        categories.add(new Category("sports",icon,"some descriptions","#839fcc",2));
        categories.add(new Category("sports",icon,"some descriptions","#ab9bcf",3));
        categories.add(new Category("sports",icon,"some descriptions","#fac384",4));
        categories.add(new Category("sports",icon,"some descriptions","#fa84c9",5));

        categoryAdapter=new CategotryAdapter(this,categories);

        GridLayoutManager layoutManager=new GridLayoutManager(this,4);
        binding.categoriesList.setLayoutManager(layoutManager);

        binding.categoriesList.setAdapter(categoryAdapter);
    }

    void initProduct(){
        products=new ArrayList<>();

        String icon="data:image/jpeg;base64,";
        products.add(new Product("TWINSBOYS\n" +
                "Collared Stylish T-shirt",icon,"instock",23.1,50,10,1));
        products.add(new Product("TWINSBOYS\n" +
                "Collared Stylish T-shirt",icon,"instock",23.1,50,10,1));
        products.add(new Product("TWINSBOYS\n" +
                "Collared Stylish T-shirt",icon,"instock",23.1,50,10,1));
        products.add(new Product("TWINSBOYS\n" +
                "Collared Stylish T-shirt",icon,"instock",23.1,50,10,1));
        products.add(new Product("TWINSBOYS\n" +
                "Collared Stylish T-shirt",icon,"instock",23.1,50,10,1));
        products.add(new Product("TWINSBOYS\n" +
                "Collared Stylish T-shirt",icon,"instock",23.1,50,10,1));
        products.add(new Product("TWINSBOYS\n" +
                "Collared Stylish T-shirt",icon,"instock",23.1,50,10,1));

        // after adding all the product do the following code.
        productAdapter = new ProductAdapter(this,products);
        GridLayoutManager layoutManager=new GridLayoutManager(this,2);
        binding.productlist.setLayoutManager(layoutManager);
        binding.productlist.setAdapter(productAdapter);
        // here productlist is the id of product recycle view which is present in main activity.

    }
}